//Copy a text to the clipboard using Clipboard API

window.copyClipboard = {
    copyText: async function (text) {
        let response;
        try { 
            await navigator.clipboard.writeText(text);
            response = text;
        }
        catch (err) {
            response = error;
        };

        return response;
    }
};

//Read text from the clipboard using Clipboard API
window.readClipboard = {
    readText: async function () {
        var response;
        try {
            response = await navigator.clipboard.readText();
        }
        catch (err) {
            response = err;
        }
        
        return response;
    }
};
